"use strict";
const canvas = document.querySelector('#canvas');
const ctx = canvas.getContext("2d");
ctx.translate(canvas.width / 2, canvas.height / 2);
ctx.strokeStyle = '#FFFFFF';
ctx.beginPath();
ctx.strokeStyle = '#DC3545';
ctx.lineWidth = 5;
ctx.moveTo(0, 300);
ctx.lineTo(0, -300);
ctx.stroke();
ctx.beginPath();
ctx.strokeStyle = '#DC3545';
ctx.lineWidth = 5;
ctx.moveTo(300, 0);
ctx.lineTo(-300, 0);
ctx.stroke();
ctx.beginPath();
ctx.strokeStyle = '#FFC107';
ctx.lineWidth = 7;
ctx.lineCap = "round";
ctx.moveTo(0, 0);
ctx.lineTo(-245, -365);
ctx.stroke();
ctx.beginPath();
ctx.strokeStyle = '#0D6EFD';
ctx.lineWidth = 7;
ctx.lineCap = "round";
ctx.moveTo(0, 0);
ctx.lineTo(76, 256);
ctx.stroke();
